INSERT INTO `om_question` VALUES ('aa413', '这个标志是何含义？', '交叉路口', '注意信号灯', '注意行人', '人行横道灯', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '413', '');
INSERT INTO `om_question` VALUES ('aa414', '这个标志是何含义？', '傍山险路', '悬崖路段', '注意落石', '危险路段', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '414', '注意落石：此标志设在左侧有落石危险的傍山路段之前适当位置。');
INSERT INTO `om_question` VALUES ('aa415', '这个标志是何含义？', '注意横风', '风向标', '隧道入口', '气象台', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '415', '注意横风：此标志设在经常有很强的侧风并有必要引起注意的路段前适当位置。');
INSERT INTO `om_question` VALUES ('aa416', '这个标志是何含义？', '急转弯路', '易滑路段', '试车路段', '曲线路段', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '416', '易滑路段：此标志设在路面的摩擦系数不能满足相应行驶速度下要求紧急刹车距离的路段前适当位置。行驶至此路段必须减速慢行。');
INSERT INTO `om_question` VALUES ('aa417', '这个标志是何含义？', '临崖路', '堤坝路', '傍山险路', '落石路', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '417', '傍山险路：用以提醒车辆驾驶人小心驾驶。设在傍山险路路段以前适当位置。');
INSERT INTO `om_question` VALUES ('aa418', '这个标志是何含义？', '堤坝路', '临崖路', '易滑路', '傍水路', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '418', '堤坝路：用以提醒车辆驾驶人小心驾驶，设在沿水库、湖泊、河流等堤坝道路以前适当位置。');
INSERT INTO `om_question` VALUES ('aa419', '这个标志是何含义？', '注意行人', '有人行横道', '村庄或集镇', '有小学校', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '419', '村庄：用以提醒车辆驾驶人小心驾驶。设在紧靠村庄、集镇且视线不良的路段以前适当位置。');
INSERT INTO `om_question` VALUES ('aa42', '驾驶人持超过有效期的驾驶证可以在1年内驾驶机动车。', '', '', '', '', '0', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:14', '2019-12-11 14:29:14', '0', null, null, null, null, '42', '《道路交通安全法实施条例》第二十八条：机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。\r\n');
INSERT INTO `om_question` VALUES ('aa420', '这个标志是何含义？', '涵洞', '水渠', '桥梁', '隧道', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '420', '隧道：用以提醒车辆驾驶人注意慢行。设在双向行驶并且照明不好的隧道口前适当位置。\r\n');
INSERT INTO `om_question` VALUES ('aa421', '这个标志是何含义？', '过水路面', '漫水桥', '渡口', '船用码头', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '421', '渡口：用以提醒车辆驾驶人谨慎驾驶。设在车辆渡口以前适当位置。');
INSERT INTO `om_question` VALUES ('aa422', '这个标志是何含义？', '不平路面', '驼峰桥', '路面高突', '路面低洼', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '422', '驼峰桥：用以提醒车辆驾驶人谨慎驾驶。设在拱度很大，影响视距的驼峰桥以前适当位置。');
INSERT INTO `om_question` VALUES ('aa423', '这个标志是何含义？', '路面低洼', '驼峰桥', '路面不平', '路面高突', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '423', '');
INSERT INTO `om_question` VALUES ('aa424', '这个标志是何含义？', '路面低洼', '驼峰桥', '路面不平', '路面高突', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '424', '');
INSERT INTO `om_question` VALUES ('aa425', '这个标志是何含义？', '路面高突', '有驼峰桥', '路面不平', '路面低洼', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '425', '');
INSERT INTO `om_question` VALUES ('aa426', '这个标志是何含义？', '过水路面', '渡口', '泥泞道路', '低洼路面', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '426', '过水路面指的是通过平时无水或流水很少的宽浅河流而修筑的在洪水期间容许水流浸过的路面。');
INSERT INTO `om_question` VALUES ('aa427', '这个标志是何含义？', '无人看守铁路道口', '有人看守铁路道口', '多股铁路与道路相交', '立交式的铁路道口', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '427', '');
INSERT INTO `om_question` VALUES ('aa428', '这个标志是何含义？', '多股铁路与道路相交', '有人看守铁路道口', '无人看守铁路道口', '注意长时鸣喇叭', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '428', '');
INSERT INTO `om_question` VALUES ('aa429', '这个标志是何含义？', '注意避让火车', '有人看守铁路道口', '无人看守铁路道口', '多股铁路与道路相交', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '429', '多股铁路与道路相交，则应在铁路道口标志上方设置叉形符号。叉形符号交叉点到警告标志三角形顶点的距离为40cm。\r\n');
INSERT INTO `om_question` VALUES ('aa43', '驾驶人的驾驶证损毁后不得驾驶机动车。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '43', '《中华人民共和国道路交通安全法实施条例》第二十八条：机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。');
INSERT INTO `om_question` VALUES ('aa430', '这个标志是何含义？', '距无人看守铁路道口50米', '距有人看守铁路道口50米', '距无人看守铁路道口100米', '距有人看守铁路道口100米', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '430', '');
