INSERT INTO `om_question` VALUES ('aa433', '这个标志是何含义？', '避让非机动车', '非机动车道', '禁止非机动车通行', '注意非机动车', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '433', '');
INSERT INTO `om_question` VALUES ('aa434', '这个标志是何含义？', '注意残疾人', '残疾人出入口', '残疾人休息处', '残疾人专用通道', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '434', '黄色标志表示注意，图中为残疾人图案，所以是提示我们注意残疾人。');
INSERT INTO `om_question` VALUES ('aa435', '这个标志是何含义？', '施工路段', '事故易发路段', '减速慢行路段', '拥堵路段', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '435', '事故易发路段：此标志设在交通事故易发路段以前适当位置。');
INSERT INTO `om_question` VALUES ('aa436', '这个标志是何含义？', '施工路段', '车多路段', '慢行', '拥堵路段', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '436', '慢行：此标志设在前方需要减速慢行的路段以前适当位置。');
INSERT INTO `om_question` VALUES ('aa437', '这个标志是何含义？', '施工路段绕行', '双向交通', '注意危险', '左右绕行', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '437', '左右绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，绕行通过。');
INSERT INTO `om_question` VALUES ('aa438', '这个标志是何含义？', '左侧绕行', '单向通行', '注意危险', '右侧绕行', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '438', '左侧绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，左侧绕行通过。');
INSERT INTO `om_question` VALUES ('aa439', '这个标志是何含义？', '单向通行', '右侧绕行', '注意危险', '左侧绕行', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '439', '右侧绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，右侧绕行通过。');
INSERT INTO `om_question` VALUES ('aa44', '记分满12分的驾驶人拒不参加学习和考试的将被公告驾驶证停止使用。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '44', '道路交通安全法实施条例》第二十五条：机动车驾驶人记分达到12分，拒不参加公安机关交通管理部门通知的学习，也不接受考试的，由公安机关交通管理部门公告其机动车驾驶证停止使用。');
INSERT INTO `om_question` VALUES ('aa440', '这个标志是何含义？', '事故多发路段', '减速慢行', '注意危险', '拥堵路段', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '440', '黄色代表注意，感叹号是前方有危险， 所以是注意危险。');
INSERT INTO `om_question` VALUES ('aa441', '这个标志是何含义？', '塌方路段', '施工路段', '前方工厂', '道路堵塞', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '441', '施工路段：用以告示前方道路施工，车辆应减速慢行或绕道行驶。\r\n');
INSERT INTO `om_question` VALUES ('aa442', '这个标志是何含义？', '建议速度', '最低速度', '最高速度', '限制速度', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '442', '此标志表示建议减速。用以提醒车辆驾驶人建议的速度行驶，设在弯道、出口、匝道的适当位置。');
INSERT INTO `om_question` VALUES ('aa443', '这个标志是何含义？', '隧道开远光灯', '隧道减速', '隧道开灯', '隧道开示宽灯', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '443', '隧道是不允许开远光灯的，此标志是用以警告车辆驾驶人进入隧道打开前照明灯，注意行驶。');
INSERT INTO `om_question` VALUES ('aa444', '这个标志是何含义？', '注意双向行驶', '靠两侧行驶', '可变车道', '注意潮汐车道', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '444', '此标志为注意潮汐车道。用以警告车辆驾驶人注意前方为潮汐车道。“潮汐车道”是指根据早晚交通流量不同情况，对有条件的道路，试点开辟潮汐车道，通过车道灯的指示方向变化，控制主干道车道行驶方向，来调整车道数。');
INSERT INTO `om_question` VALUES ('aa445', '这个标志是何含义？', '注意保持车距', '车距确认路段', '车速测试路段', '两侧变窄路段', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '445', '典型的警告标志，此标志表示注意保持车距。用以警告车辆驾驶人注意和前车保持安全距离。');
INSERT INTO `om_question` VALUES ('aa446', '这个标志是何含义？', '注意交互式道路', '注意分离式道路', '平面交叉路口', '环行平面交叉', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '446', '此标志表示注意分离式道路。用以警告车辆驾驶人注意前方交叉的被交道路是分离式道路。');
INSERT INTO `om_question` VALUES ('aa447', '这个标志是何含义？', 'Y型交叉口', '主路让行', '注意分流', '注意合流', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '447', '黄底为警告标志，所以排除A和B，很明显粗的那个直线表示的是主干线，细的那条表示的是分车道。二者交汇，合流标志，选择D。\r\n');
INSERT INTO `om_question` VALUES ('aa448', '这个标志是何含义？', '避险车道', '应急车道', '路肩', '急弯道', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '448', '避险车道是指在下长陡坡路段时，行车道外侧增设的供速度失控车辆驶离正线安全减速的专用车道。\r\n');
INSERT INTO `om_question` VALUES ('aa449', '这属于哪一种标志？', '警告标志', '禁令标志', '指示标志', '指路标志', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '449', '黄色-警示、红色-禁令、蓝色-指示指路，黑色-辅助。');
INSERT INTO `om_question` VALUES ('aa45', '驾驶人的机动车驾驶证被依法扣留、暂扣的情况下不得驾驶机动车。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '45', '《道路交通安全法实施条例》第二十八条：机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。\r\n');
INSERT INTO `om_question` VALUES ('aa450', '禁令标志的作用是什么？', '禁止或限制行为', '告知方向信息', '指示车辆行进', '警告前方危险', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '450', '禁令标志是交通标志中主要标志的一种，对车辆加以禁止或限制的标志，如禁止通行、禁止停车、禁止左转弯、禁止鸣喇叭、限制速度、限制重量等。');
INSERT INTO `om_question` VALUES ('aa451', '这个标志是何含义？', '不准车辆驶入', '不准长时间停车', '停车让行', '不准临时停车', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '451', '');
INSERT INTO `om_question` VALUES ('aa452', '这个标志是何含义？', '不准让行', '会车让行', '停车让行', '减速让行', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '452', '');
INSERT INTO `om_question` VALUES ('aa453', '这个标志是何含义？', '会车时停车让对方车先行', '前方是双向通行路段', '右侧道路禁止车通行', '会车时停车让右侧车先行', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '453', '会车时停车让对方车先行：表示车辆会车时，必须停车让对方车先行。设置在会车有困难的狭窄路段的一端或由于某种原因只能开放一条车道作双向通行路段的一端。');
INSERT INTO `om_question` VALUES ('aa454', '这个标志是何含义？', '禁止驶入', '禁止通行', '减速行驶', '限时进入', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '454', '此标志表示禁止通行，禁止一切车辆和行人通行。');
INSERT INTO `om_question` VALUES ('aa455', '这个标志提示哪种车型禁止通行？', '中型客车', '小型货车', '各种车辆', '小型客车', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '455', '此标志表示禁止小型客车驶入。轿车属于小型客车');
INSERT INTO `om_question` VALUES ('aa456', '这个标志是何含义？', '禁止向左转弯', '禁止驶入左车道', '禁止车辆掉头', '禁止向左变道', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '456', '禁止向左转弯：表示前方路口禁止一切车辆向左转弯。此标志设在禁止向左转弯的路口前适当位置。');
INSERT INTO `om_question` VALUES ('aa457', '这个标志是何含义？', '禁止驶入路口', '禁止向右转弯', '禁止车辆掉头', '禁止变更车道', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '457', '禁止向右转弯：表示前方路口禁止一切车辆向右转弯。此标志设在禁止向右转弯的路口前适当位置。');
INSERT INTO `om_question` VALUES ('aa458', '这个标志是何含义？', '禁止掉头', '禁止向右转弯', '禁止直行', '禁止向左转弯', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '458', '禁止直行：表示前方路口禁止一切车辆直行。此标志设在禁止直行的路口前适当位置。');
INSERT INTO `om_question` VALUES ('aa459', '这个标志是何含义？', '禁止在路口掉头', '禁止向左向右变道', '禁止车辆直行', '禁止向左向右转弯', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '459', '禁止向左向右转弯：表示前方路口禁止一切车辆向左向右转弯。此标志设在禁止向左向右转弯的路口前适当位置。');
INSERT INTO `om_question` VALUES ('aa46', '前方路口这种信号灯亮表示什么意思？', '路口警示', '禁止通行', '准许通行', '提醒注意', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '46', '红灯停、绿灯行、此时前方是红灯，所以是禁止通行的。');
INSERT INTO `om_question` VALUES ('aa460', '这个标志是何含义？', '禁止直行和向左转弯', '禁止直行和向左变道', '允许直行和向左变道', '禁止直行和向右转弯', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '460', '禁止直行和向左转弯：表示前方路口禁止一切车辆直行和向左转弯。此标志设在禁止直行和向左转弯的路口前适当位置。\r\n');
INSERT INTO `om_question` VALUES ('aa461', '这个标志是何含义？', '禁止直行和向左转弯', '禁止直行和向左变道', '允许直行和向左变道', '禁止直行和向右转弯', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '461', '禁止直行和向右转弯：表示前方路口禁止一切车辆直行和向右转弯。此标志设在禁止直行和向右转弯的路口前适当位置。');
INSERT INTO `om_question` VALUES ('aa462', '这个标志是何含义？', '禁止直行', '禁止掉头', '禁止变道', '禁止左转', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '462', '禁止掉头：表示前方路口禁止一切车辆掉头。此标志设在禁止掉头的路口前适当位置。');
INSERT INTO `om_question` VALUES ('aa463', '这个标志是何含义？', '禁止借道', '禁止变道', '禁止超车', '禁止掉头', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '463', '禁止超车：表示该标志至前方解除禁止超车标志的路段内，不准机动车超车。此标志设在禁止超车的起点。');
INSERT INTO `om_question` VALUES ('aa464', '这个标志是何含义？', '解除禁止借道', '解除禁止变道', '准许变道行驶', '解除禁止超车', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '464', '');
INSERT INTO `om_question` VALUES ('aa465', '这个标志是何含义？', '允许长时停车', '允许临时停车', '禁止长时停车', '禁止停放车辆', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '465', '');
INSERT INTO `om_question` VALUES ('aa466', '这个标志是何含义？', '允许长时停车', '禁止临时停车', '禁止长时停车', '禁止停放车辆', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '466', '');
INSERT INTO `om_question` VALUES ('aa467', '这个标志是何含义？', '禁止长时鸣喇叭', '断续鸣喇叭', '禁止鸣喇叭', '减速鸣喇叭', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '467', '禁止鸣喇叭：表示禁止鸣喇叭。此标志设在需要禁止鸣喇叭的地方。禁止鸣喇叭的时间和范围可用辅助标志说明。');
INSERT INTO `om_question` VALUES ('aa468', '这个标志是何含义？', '解除3米限宽', '限制高度为3米', '预告宽度为3米', '限制宽度为3米', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '468', '限制宽度为3米：表示禁止装载宽度超过标志所示数值的车辆通行。此标志设在最大允许宽度受限制的地方。 以图为例：装载宽度不得超过3米。');
INSERT INTO `om_question` VALUES ('aa469', '这个标志是何含义？', '限制高度为3.5米', '限制宽度为3.5米', '解除3.5米限高', '限制车距为3.5米', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '469', '限制高度为3.5米：表示禁止装载高度超过标志所示数值的车辆通行。此标志设在最大允许高度受限制的地方。 以图为例：装载高度不得超过3.5米。');
