INSERT INTO `om_question` VALUES ('aa49fb480a-0857-49cc-928b-a6336c92792e', '如图所示，夜间驾驶机动车遇对方使用远光灯，无法看清前方路况时，以下做法正确的是什么？', '保持行驶方向和车速不变', '自己也打开远光灯行驶', '降低车速，谨慎会车', '加速通过，尽快摆脱眩目光线', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '1108', '夜间驾驶机动车遇对方使用远光灯，无法看清前方路况时，降低车速，谨慎会车，以免发生车祸、危险。');
INSERT INTO `om_question` VALUES ('aa4bc75abb-2ad6-4a73-9b5d-ba5181f6caa4', '如图所示，驾驶机动车驶出这个路口时应当怎样使用灯光?', '开启右转向灯', '开启危险报警闪光灯', '不用开启转向灯', '开启左转向灯', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '1174', '图中是环岛，驶入开左转向灯，驶出开右转向灯。');
INSERT INTO `om_question` VALUES ('aa4c95f7b3-a7f0-43ee-b6cb-b9b5ddff554d', '道路没有划分机动车道、非机动车道和人行道的，以下说法正确的是什么？', '机动车在道路左侧通行，非机动车和行人随意通行', '机动车在道路左侧通行，非机动车和行人在道路两侧通行', '机动车在道路中间通行，非机动车和行人在道路两侧通行', '机动车、非机动车和行人可随意通行', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '1213', '按规定：驾驶机动车经过无划分车道的道路时，机动车在道路中间通行，非机动车和行人在道路两侧通行。');
INSERT INTO `om_question` VALUES ('aa4f4bc135-46fe-4f2e-8d1f-a1474695c0df', '如下图所示的交通事故中，有关事故责任认定，正确的说法是什么？', 'B车闯红灯，所以B负全责', 'B车可以右转，但不得妨碍被放行的直行车辆，所以B车负全责', '直行车辆不得妨碍右转车辆，所以A车负全责', '右侧方向的车辆具有优先通行权，故A车负全责', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '1281', '看红绿灯，A车是直行车道享有最高通行权利，而B车与A车相撞，明显影响了直行车的正常同行，所以B车负全责。');
INSERT INTO `om_question` VALUES ('aa4fe37781-caf5-4e4e-bffe-5b41dad66797', '非机动车驾驶人、行人故意碰撞机动车造成交通事故的，机动车一方不承担赔偿责任。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '1091', '《中华人民共和国道路交通安全法》第七十六条：明确规定交通事故的损失是由非机动车驾驶人、行人故意碰撞机动车造成的，机动车一方不承担赔偿责任。\r\n');
INSERT INTO `om_question` VALUES ('aa5', '驾驶机动车在道路上违反道路通行规定应当接受相应的处罚。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '5', '违反道路通行规定势必要受到相应处罚的，避免下次再犯。');
INSERT INTO `om_question` VALUES ('aa50', '交通标志和交通标线不属于交通信号。', '', '', '', '', '0', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:14', '2019-12-11 14:29:14', '0', null, null, null, null, '50', '《公安部令第123号》第二十五条规定：交通信号包括交通信号灯、交通标志、交通标线和交通警察的指挥。\r\n');
INSERT INTO `om_question` VALUES ('aa500', '这个标志是何含义？', '直行和左转合用车道', '直行和掉头合用车道', '直行和右转车道', '分向行驶车道', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '500', '表示只准一切车辆直行和向左转弯。此标志设在车辆必须直行和向左转弯的路口以前适当位置。');
INSERT INTO `om_question` VALUES ('aa501', '这个标志是何含义？', '左转车道', '掉头车道', '绕行车道', '分向车道', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '501', '此标志表示该车道为掉头车道。');
INSERT INTO `om_question` VALUES ('aa502', '这个标志是何含义？', '直行和左转合用车道', '禁止左转和掉头车道', '掉头和左转合用车道', '分向行驶车道', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '502', '图中有左转和掉头标志，所以是掉头和左转合用车道。');
INSERT INTO `om_question` VALUES ('aa503', '这个标志是何含义？', '直线行驶车道', '左转行驶车道', '右转行驶车道', '分向行驶车道', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '503', '此标志表示分向行驶车道。同向多车道的公路一般都设计了分向行驶车道，正常行驶过程中可以变更车道，当你行驶至路口时要变换到你要行驶的车道。例如要左转弯时要提前变更车道至左转弯车道；要右转弯时要提前变更车道至右转弯车道，不能占用他人行车道，以最大限度的保障通行。\r\n');
INSERT INTO `om_question` VALUES ('aa504', '这个标志是何含义？', '公交线路专用车道', '大型客车专用车道', '快速公交系统专用车道', '多乘员车辆专用车道', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '504', '');
INSERT INTO `om_question` VALUES ('aa505', '这个标志是何含义？', '禁止小型车行驶', '机动车行驶', '只准小型车行驶', '不准小型车通行', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '505', '');
INSERT INTO `om_question` VALUES ('aa506', '这个标志是何含义？', '小型车车道', '小型车专用车道', '机动车车道', '多乘员车辆专用车道', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '506', '');
INSERT INTO `om_question` VALUES ('aa507', '这个标志是何含义？', '非机动车停车位', '电动自行车行驶', '非机动车停放区', '非机动车行驶', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '507', '');
INSERT INTO `om_question` VALUES ('aa508', '这个标志是何含义？', '非机动车车道', '禁止自行车通行车道', '自行车专用车道', '停放自行车路段', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '508', '');
INSERT INTO `om_question` VALUES ('aa509', '这个标志是何含义？', '公交车专用车道', 'BRT车辆专用车道', '大型客车专用车道', '多乘员车专用车道', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '509', 'BRT指的就是指城市快速公交。');
INSERT INTO `om_question` VALUES ('aa50d5d88b-24ee-41c0-b17b-3ed77f71876e', '机动车在交叉路口不得超车的原因是什么？', '路口有交通监控设备', '路口设有信号灯', '路口内交通情况复杂，易发生交通事故', '机动车速度慢，不足以超越前车', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '1326', '路口交通情况较为复杂，冒然超车，容易引发交通事故。安全起见，不要在路口超车。\r\n');
INSERT INTO `om_question` VALUES ('aa51', '交通信号灯由红灯、绿灯和黄灯组成。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '51', '《公安部令第123号》第二十六条：交通信号灯由红灯、绿灯、黄灯组成。红灯表示禁止通行，绿灯表示准许通行，黄灯表示警示。\r\n');
INSERT INTO `om_question` VALUES ('aa510', '这个标志是何含义？', '小型汽车专用车道', '机动车专用车道', '多乘员车辆专用车道', '出租汽车专用车道', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '510', '此标志为多乘员车辆专用车道，比机动车专用车道标志上的小车多了2个小人，所以它是多乘员车辆专用车道。');
INSERT INTO `om_question` VALUES ('aa511', '这个标志是何含义？', '掉头', '倒车', '左转', '绕行', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '511', '');
INSERT INTO `om_question` VALUES ('aa512', '这属于哪一类标志？', '警告标志', '禁令标志', '指示标志', '指路标志', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '512', '红色是禁令标志，黄色是警告标志，蓝色是指示标志与普通路段的指路标志，绿色是高速公路的指路标志。\r\n而且一般指路标志上面有字，指示标志只有图。所以本题是指路标志。');
INSERT INTO `om_question` VALUES ('aa513', '指路标志的作用是什么？', '限制车辆通行', '提示限速信息', '提供方向信息', '警告前方危险', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '513', '指路标志是传递道路方向，地点，距离信息的标志。');
INSERT INTO `om_question` VALUES ('aa514', '这个标志是何含义？', '交叉路口预告', '车道方向预告', '分道信息预告', '分岔处预告', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '514', '此标志表示大交通量的四车道以上公路交叉路口预告。分向行驶车道边上是虚线，下面没有距离标志。交叉路口标志是这个，有距离的数字。');
INSERT INTO `om_question` VALUES ('aa515', '这个标志是何含义？', '地点和距离预告', '十字交叉路口预告', '分道信息预告', '道路分岔处预告', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '515', '');
INSERT INTO `om_question` VALUES ('aa516', '这个标志是何含义？', 'Y型交叉路口预告', '十字交叉路口预告', '丁字交叉路口预告', '道路分叉处预告', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '516', '');
INSERT INTO `om_question` VALUES ('aa517', '这个标志是何含义？', '环行交叉路口预告', '十字交叉路口预告', '丁字交叉路口预告', 'Y型交叉路口预告', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '517', '');
INSERT INTO `om_question` VALUES ('aa518', '这个标志是何含义？', '环行交叉路口预告', '十字交叉路口预告', '互通立体交叉预告', 'Y型交叉路口预告', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '518', '');
INSERT INTO `om_question` VALUES ('aa519', '这个标志是何含义？', '十字交叉路口预告', '互通式立体交叉预告', 'Y型交叉路口预告', '环行交叉路口预告', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '519', '');
INSERT INTO `om_question` VALUES ('aa52', '道路交通标线分为指示标线、警告标线、禁止标线。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '52', '道路交通标志 道路交通标志和标线是用图案、符号、文字传递交通管理信息，用以管制及引导交通的一种安全管理设施。《道路交通标志和标线》规定的交通标志分为七大类：\r\n(1)警告标志：警告车辆和行人注意危险地点的标志。\r\n(2)禁令标志：禁止或限制车辆、行人交通行为的标志。\r\n(3)指示标志：指示车辆、行人行进的标志。\r\n(4)指路标志：传递道路方向、地点、距离的标志。\r\n(5)旅游区标志：提供旅游景点方向、距离的标志。\r\n(6)道路施工安全标志：通告道路施工区通行的标志。\r\n(7)辅助标志：附设于主标志下起辅助说明使用的标志。\r\n规定的道路交通标线分为三大类：\r\n(1)指示标线：指示车行道、行车方向、路面边缘、人行道等设施的标线。\r\n(2)禁止标线：告示道路交通的遵行、禁止、限制等特殊规定，车辆驾驶人员及行人需要严格遵守的标线。\r\n(3)警告标线：促使车辆驾驶人员及行人了解道路上的特殊情况，提高警觉，准备防范应变措施的标线。');
INSERT INTO `om_question` VALUES ('aa520', '这个标志是何含义？', '国道编号', '省道编号', '县道编号', '乡道编号', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '520', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底S开头的，国道是红底G开头的。');
INSERT INTO `om_question` VALUES ('aa520c7f02-f3e6-4b52-be41-175f87b21059', '为提示车辆和行人注意，雾天必须开启哪个灯？', '图1', '图2', '图3', '图4', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '1297', '图1为清洗液指示灯；图2为远光指示灯；图3为转向指示灯；图4为雾灯指示灯。雾天当然要开雾灯啦。\r\n');
INSERT INTO `om_question` VALUES ('aa521', '这个标志是何含义？', '国道编号', '省道编号', '县道编号', '乡道编号', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '521', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底S开头的，国道是红底G开头的。');
INSERT INTO `om_question` VALUES ('aa522', '这个标志是何含义？', '国道编号', '省道编号', '县道编号', '乡道编号', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '522', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底S开头的，国道是红底G开头的。');
INSERT INTO `om_question` VALUES ('aa523', '这个标志是何含义？', '国道编号', '省道编号', '县道编号', '乡道编号', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '523', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底S开头的，国道是红底G开头的。');
INSERT INTO `om_question` VALUES ('aa524', '这个标志是何含义？', '地点距离', '行驶路线', '行驶方向', '终点地名', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '524', '标志上标出了地名和千米数，可以知道这是地点距离标志。如果将千米数变成箭头，则是行驶方向标志。\r\n');
