INSERT INTO `om_question` VALUES ('aa539', '这个标志是何含义？', '左侧通行', '不准通行', '两侧通行', '右侧通行', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '539', '');
INSERT INTO `om_question` VALUES ('aa54', '道路上划设这种标线的车道内允许下列哪类车辆通行？', '出租车', '公务用车', '公交车', '私家车', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '54', '请注意地面的字，公交专用车道。所以是只有公交车才能行驶。\r\n');
