INSERT INTO `om_question` VALUES ('aa541', '这个标志是何含义？', '高速公路终点预告', '高速公路入口预告', '高速公路起点预告', '高速公路出口预告', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '541', '');
INSERT INTO `om_question` VALUES ('aa542', '这个标志是何含义？', '高速公路右侧出口预告', '高速公路下一出口预告', '高速公路地点、方向预告', '高速公路左侧出口预告', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '542', '图标的意思应该是在下一个高速路口沿右行驶是去济南的高速公路，沿左方向行驶是去天津的高速公路，这个就是进入高速路口收费站后选择目的地的方向行驶指示。\r\n');
INSERT INTO `om_question` VALUES ('aa543', '这个标志是何含义？', '高速公路终点地名预告', '高速公路行驶路线预告', '高速公路行驶方向预告', '高速公路地点距离预告', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '543', '');
INSERT INTO `om_question` VALUES ('aa544', '这个标志是何含义？', '高速公路界牌编号', '高速公路里程编号', '高速公路命名编号', '高速公路路段编号', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '544', '国家高速公路公路网的命名规则，编号由字母标识符汉语拼音“G”和阿拉伯数字编号组成。G2为京沪国家高速。\r\n');
INSERT INTO `om_question` VALUES ('aa545', '这个标志是何含义？', '高速公路下一出口预告', '高速公路右侧出口预告', '高速公路目的地预告', '高速公路左侧出口预告', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '545', '下一高速公路出口预告，还有4公里，预告了下一个高速公路的出口和距离，所以选择“高速公路下一出口预告”。');
