INSERT INTO `om_question` VALUES ('aa548', '这个标志是何含义？', '高速公路起点', '高速公路出口', '高速公路入口', '高速公路终点', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '548', '');
