INSERT INTO `om_question` VALUES ('aa562', '这属于哪一种标志？', '旅游区标志', '作业区标志', '告示标志', '高速公路标志', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '562', '旅游区标志：设在通往旅游区各连接道路的交叉口处。');
INSERT INTO `om_question` VALUES ('aa563', '这个标志是何含义？', '旅游区类别', '旅游区距离', '旅游区方向', '旅游区符号', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '563', '棕底白字指的是旅游区预告，图上又指明了距离，那么本题的标志是指旅游区距离。');
INSERT INTO `om_question` VALUES ('aa564', '这个标志是何含义？', '旅游区类别', '旅游区距离', '旅游区方向', '旅游区符号', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '564', '此图是旅游区标志，而箭头指示的是方向，所以是旅游区方向指示。\r\n');
INSERT INTO `om_question` VALUES ('aa565', '这个标志是何含义？', '提醒车辆驾驶人前方道路沿水库、湖泊、河流', '提醒车辆驾驶人前方有向上的陡坡路段', '提醒车辆驾驶人前方有向下的陡坡路段', '提醒车辆驾驶人前方有两个及以上的连续上坡路段', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '565', '下陡坡：此标志设在纵坡度在7%和市区纵坡度大于4%的陡坡道路前适当位置。用以提醒车辆驾驶人小心驾驶。\r\n');
INSERT INTO `om_question` VALUES ('aa566', '这个标志是何含义？', '提醒车辆驾驶人前方有两个及以上的连续上坡路段', '提醒车辆驾驶人前方有向上的陡坡路段', '提醒车辆驾驶人前方有向下的陡坡路段', '提醒车辆驾驶人前方有两个及以上的连续下坡路段', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '566', '连续下坡：用以提醒车辆驾驶人小心驾驶。当连续下坡总长大于3 km后，应重复设置。\r\n');
INSERT INTO `om_question` VALUES ('aa567', '这种标志的作用是警告车辆驾驶人前方有危险，谨慎通行。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '567', '黄色标志都是表示警告，第一张图警告前方T字路口，第二张图片表示急转弯，第三张图片表示前方道路变窄，要小心驾驶。\r\n\r\n');
INSERT INTO `om_question` VALUES ('aa568', '这个标志的作用是用以警告车辆驾驶人谨慎慢行，注意横向来车。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '568', '交叉路口标志的作用是：警告车辆驾驶人谨慎慢行，注意横向来车。');
