INSERT INTO `om_question` VALUES ('aa875', '机动车仪表板上（如图所示）亮表示什么？', '危险报警闪光灯开启', '前照灯开启', '前后位置灯开启', '前后雾灯开启', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '875', '汽车的位置灯就是车辆示宽灯，当开启双跳灯后，前后左右四个闪的灯。所以图示灯亮了，表示前后位置灯开启。');
