INSERT INTO `om_question` VALUES ('aa878', '打开位置灯开关，（如图所示）亮起。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '878', '该指示灯是用来显示车辆示宽灯的工作状态，平时为熄灭状态，当示宽灯打开时，该指示灯随即点亮。当示宽灯关闭或者关闭示宽灯打开大灯时，该指示灯自动熄灭。');
INSERT INTO `om_question` VALUES ('aa879', '发动机起动后仪表板上（如图所示）亮表示什么？', '发动机机油压力过高', '发动机主油道堵塞', '发动机机油压力过低', '发动机曲轴箱漏气', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '879', '图中的标志是机油的标志。 该指示灯用来显示发动机内机油的压力状况，该指示灯常亮，说明该车发动机机油压力低于规定标准，需要维修。');
INSERT INTO `om_question` VALUES ('aa87b9de5f-d028-4ad9-9a00-f1339616ff5f', '隐瞒有关情况或者提供虚假材料申请机动车驾驶证，申请人在多少年内不得再次申领机动车驾驶证？', '1年', '2年', '3年', '4年', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '1127', '《机动车驾驶证申领和使用规定》第七十八条：申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效；申请人在一年内不得再次申领机动车驾驶证。');
INSERT INTO `om_question` VALUES ('aa88', '在这条城市道路上行驶的最高速度不能超过多少？', '30公里/小时', '40公里/小时', '50公里/小时', '70公里/小时', 'C', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '88', '《中华人民共和国道路交通安全法实施条例》第四十五条：机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：\r\n(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；\r\n(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。\r\n');
INSERT INTO `om_question` VALUES ('aa880', '机动车仪表板上（如图所示）亮表示发动机可能机油量不足。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '880', '图中的标志是机油的标志。 该指示灯用来显示发动机内机油的压力状况，该指示灯常亮，说明该车发动机机油压力低于规定标准，需要维修。');
INSERT INTO `om_question` VALUES ('aa881', '机动车仪表板上（如图所示）亮表示发动机可能机油压力过高。', '', '', '', '', '0', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:14', '2019-12-11 14:29:14', '0', null, null, null, null, '881', '图中的标志是机油的标志。 该指示灯用来显示发动机内机油的压力状况，该指示灯常亮，说明该车发动机机油压力低于规定标准，需要维修。');
INSERT INTO `om_question` VALUES ('aa882', '机动车仪表板上（如图所示）亮表示什么？', '制动踏板没回位', '驻车制动解除', '行车制动器失效', '制动系统出现异常', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '882', '括号圆圈里面是“P” 表示驻车制动器处于制动状态，即手刹没松开（但是车辆没有发生故障）。\r\n里面是“！” 表示制动系统异常或故障。');
INSERT INTO `om_question` VALUES ('aa883', '机动车仪表板上（如图所示）亮表示什么？', '防抱死制动系统故障', '驻车制动器处于解除状态', '安全气囊处于故障状态', '行车制动系统故障', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '883', '防抱死制动系统出现故障标志，出现此标志，应停车检查防抱死系统。');
INSERT INTO `om_question` VALUES ('aa884', '机动车仪表板上（如图所示）亮表示什么？', '行车制动系统出现故障', '驻车制动器处于制动状态', '防抱死制动系统出现故障', '驻车制动器处于解除状态', 'B', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '884', '圆圈里面是“P” 表示驻车制动器处于制动状态，车辆没有发生故障。\r\n里面是“！” 表示制动系统异常或故障。');
INSERT INTO `om_question` VALUES ('aa885', '机动车仪表板上（如图所示）亮，表示驻车制动器操纵杆可能没松到底。', '', '', '', '', '0', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:14', '2019-12-11 14:29:14', '0', null, null, null, null, '885', '圆圈里面是“P” 表示驻车制动器处于制动状态，即手刹没松开，但是车辆没有发生故障。\r\n里面是“！” 表示制动系统异常或故障。');
INSERT INTO `om_question` VALUES ('aa886', '机动车仪表板上（如图所示）亮，表示行车制动系统可能出现故障。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '886', '圆圈里面是“P” 表示驻车制动器处于制动状态，即手刹没松开，但是车辆没有发生故障。\r\n里面是“！” 表示制动系统异常或故障。\r\n');
INSERT INTO `om_question` VALUES ('aa887', '机动车仪表板上（如图所示）亮时，不影响正常行驶。', '', '', '', '', '0', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:14', '2019-12-11 14:29:14', '0', null, null, null, null, '887', '报警灯是驻车制动器及系统故障指示灯。驻车制动器操纵杆拉起时，指示灯亮，颜色为红色；松开后，指示灯熄灭。行车途中该灯亮起，表示制动系统出了问题或故障。不能上路。');
INSERT INTO `om_question` VALUES ('aa888', '机动车仪表板上（如图所示）亮时，防抱死制动系统处于打开状态。', '', '', '', '', '0', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:14', '2019-12-11 14:29:14', '0', null, null, null, null, '888', '此标志为防抱死制动系统出现故障，应该停车进行全面检查。');
INSERT INTO `om_question` VALUES ('aa889', '机动车仪表板上（如图所示）亮时，表示驻车制动器处于制动状态。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '889', '圆圈里面是“P” 表示驻车制动器处于制动状态，即手刹没松开，但是车辆没有发生故障。\r\n里面是“！” 表示制动系统异常或故障。');
INSERT INTO `om_question` VALUES ('aa89', '在这条公路上行驶的最高速度不能超过多少？', '30公里/小时', '40公里/小时', '50公里/小时', '70公里/小时', 'D', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '89', '《中华人民共和国道路交通安全法实施条例》第四十五条：机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：\r\n(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；\r\n(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。\r\n');
