INSERT INTO `om_question` VALUES ('aa962', '机动车发生碰撞时座椅安全带主要作用是什么？', '减轻驾乘人员伤害', '保护驾乘人员腰部', '保护驾乘人员颈部', '保护驾乘人员胸部', 'A', '2', '1', '626debde10144b409189a8246e73f7b6', '科目一', null, null, null, '2019-12-11 13:11:15', '0', null, null, null, null, '962', '安全带在紧急情况下，可以迅速拉紧把人束缚在座椅上，保证安全。可以减轻对驾乘人员的伤害。');
INSERT INTO `om_question` VALUES ('aa963', '机动车发生正面碰撞时，安全气囊加上安全带的双重保护才能充分发挥作用。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '963', '研究表明，假设气囊加安全带是100%安全的话，安全带的作用是70%，气囊的作用是30%。');
INSERT INTO `om_question` VALUES ('aa964', '机动车在发生碰撞时，安全带可以减轻驾乘人员伤害。', '', '', '', '', '1', '2', '3', '626debde10144b409189a8246e73f7b6', '科目一', null, null, '2019-12-11 14:29:00', '2019-12-11 14:29:00', '0', null, null, null, null, '964', '安全带加上安全气囊的双重保护才能充分发挥作用。');
