mapper.select:
    select
    
      distinct
    
    
    from  (
	    select t.plan_id, t.plan_name, t.paper_id, t.create_time, t.update_time,t.auto_mark_flag,t.plan_type,t.plan_time,
     t1.paper_name, t1.paper_score, t1.paper_time, t1.add_mode, t1.single_count, t1.multi_count, 
    t1.judge_count, t1.blank_count, t1.answer_count, t1.case_count, t.course_id, t.course_name, passing_score, passing_type
		from om_exam_plan t, om_paper t1 where t.paper_id=t1.paper_id and t.deleted='0' and t1.deleted='0'
	 ) as t
    
      
    
    
      order by ${orderByClause}
    
    
	  limit ${limitStart}, ${limitPageSize}

    select 
    
    from  (
	    select t.plan_id, t.plan_name, t.paper_id, t.create_time, t.update_time,t.auto_mark_flag,t.plan_type,t.plan_time,
     t1.paper_name, t1.paper_score, t1.paper_time, t1.add_mode, t1.single_count, t1.multi_count, 
    t1.judge_count, t1.blank_count, t1.answer_count, t1.case_count, t.course_id, t.course_name, passing_score, passing_type
		from om_exam_plan t, om_paper t1 where t.paper_id=t1.paper_id and t.deleted='0' and t1.deleted='0'
	 ) as t
    where plan_id = #{planId,jdbcType=VARCHAR}
